	 (3,10,30,'Avanzamento Iter','Azione','Trasmetti al Gestore Contratto','select 
case
  when exists(
        select 1 
        from sirtpl_contratti.sirtplc_t_contratto c, rebus.rebusp_r_proc_contratto pc, rebus.rebusp_t_procedimento p, 
           rebus.rebusp_t_iter_procedimento ip, sirtpl_trasv.sirtpl_r_utente_sog_giurid usg, sirtpl_contratti.sirtplc_r_contratto_raggrupp cr       
        where c.id_contratto = pc.id_contratto
        and pc.id_procedimento = p.id_procedimento
        and p.id_procedimento = ip.id_procedimento and ip.id_stato_iter = 10
        and ip.id_utente_sog_giurid = usg.id_utente_sog_giurid
        and usg.id_soggetto_giuridico = cr.id_soggetto_giuridico
        and cr.id_contratto = c.id_contratto
        and cr.capofila = false         -- non è capofila in un raggruppamento
        and p.id_procedimento = $$ID_PROC
        )
    OR exists( -- il SG a nome del quale utente ha aperto il procedimento.... 
        select 1 
        from sirtpl_contratti.sirtplc_t_contratto c, rebus.rebusp_r_proc_contratto pc, rebus.rebusp_t_procedimento p, 
           rebus.rebusp_t_iter_procedimento ip, sirtpl_trasv.sirtpl_r_utente_sog_giurid usg, sirtpl_contratti.sirtplc_r_sost_sog_contr ssc      
        where c.id_contratto = pc.id_contratto
        and pc.id_procedimento = p.id_procedimento
        and p.id_procedimento = ip.id_procedimento and ip.id_stato_iter = 10
        and ip.id_utente_sog_giurid = usg.id_utente_sog_giurid
        and usg.id_soggetto_giuridico = ssc.id_sog_giurid_esecutore -- ... è subaffidataria del SG esecutore del contratto (quindi trasmetto a qs)
        and ssc.id_contratto = c.id_contratto
        and ssc.id_tipo_sostituzione = 1
        and p.id_procedimento = $$ID_PROC
        )
    OR exists( -- il SG a nome del quale utente ha aperto il procedimento.... 
          select 1 
          from sirtpl_contratti.sirtplc_t_contratto c, rebus.rebusp_r_proc_contratto pc, rebus.rebusp_t_procedimento p, 
             rebus.rebusp_t_iter_procedimento ip, sirtpl_trasv.sirtpl_r_utente_sog_giurid usg, 
             sirtpl_contratti.sirtplc_r_contratto_raggrupp cr, sirtpl_contratti.sirtplc_r_sost_sog_contr_raggr sscr       
          where c.id_contratto = pc.id_contratto
          and pc.id_procedimento = p.id_procedimento
          and p.id_procedimento = ip.id_procedimento and ip.id_stato_iter = 10
          and ip.id_utente_sog_giurid = usg.id_utente_sog_giurid
          and usg.id_soggetto_giuridico = sscr.id_soggetto_giuridico -- ... è subaffidataria o è un subentro in un raggruppamento
          and cr.capofila = false                   -- di un SG che non è capofila
          and sscr.id_contratto_raggrupp = cr.id_contratto_raggrupp
          and sscr.id_tipo_sostituzione in (1,2)
          and cr.id_contratto = c.id_contratto
          and p.id_procedimento = $$ID_PROC
        )
    OR exists
        (-- procedimento di sostituzione
         select 1 
         from sirtpl_contratti.sirtplc_t_contratto c, rebus.rebusp_t_sub_procedimento sp, rebus.rebusp_r_proc_contratto pc, rebus.rebusp_t_procedimento p, 
            rebus.rebusp_t_procedimento p_sub, rebus.rebusp_t_iter_procedimento ip, sirtpl_trasv.sirtpl_r_utente_sog_giurid usg 
         where p.id_tipo_procedimento = 2 and p.id_procedimento = sp.id_procedimento
         and sp.id_sub_procedimento_1 = p_sub.id_procedimento -- ne prendo uno dei due; assumo (perchè vincolato da applicativo) che riferiscano allo stesso contratto
         and p_sub.id_procedimento = pc.id_procedimento
         and pc.id_contratto = c.id_contratto
         and p_sub.id_procedimento = ip.id_procedimento and ip.id_stato_iter = 10
         and ip.id_utente_sog_giurid = usg.id_utente_sog_giurid
         and usg.id_soggetto_giuridico != c.id_sog_giurid_esecutore   -- non è il SG esecutore
         and p.id_procedimento = $$ID_PROC
         and (-- è subaffidataria del SG esecutore del contratto
            exists(select 1 from sirtpl_contratti.sirtplc_r_sost_sog_contr ssc
                  where ssc.id_contratto = c.id_contratto and ssc.id_sog_giurid_esecutore = usg.id_soggetto_giuridico and ssc.id_tipo_sostituzione = 1)
            or -- oppure è in un raggruppamento, e non è capofila
            exists(select 1 from sirtpl_contratti.sirtplc_r_contratto_raggrupp cr
                 where c.id_contratto = cr.id_contratto 
                 and cr.id_soggetto_giuridico = usg.id_soggetto_giuridico and cr.capofila = false)
            or -- oppure è subaffidataria o è un subentro di un SG non capofila in un raggruppamento 
            exists(select 1 from sirtpl_contratti.sirtplc_r_contratto_raggrupp cr, sirtpl_contratti.sirtplc_r_sost_sog_contr_raggr sscr
                 where c.id_contratto = cr.id_contratto and cr.id_contratto_raggrupp = sscr.id_contratto_raggrupp 
                 and sscr.id_soggetto_giuridico = usg.id_soggetto_giuridico and sscr.id_tipo_sostituzione in (1,2))
          )
        )
    then 1
    else 0
end active
from dual',false,13,'2020-06-16',NULL,true),
